casper.test.begin("Twitter Search Feed large input test",function(t){casper.open("http://localhost/~dave/twittersearchfeed/",function(){casper.evaluate(function(){localStorage.removeItem("searchQuery")})}),casper.waitForSelector("form input#query",function(){t.assertExists("form input#query"),this.click("form input#query")},function(){t.assertExists("form input#query")}),casper.waitForSelector("input#query",function(){this.sendKeys("input#query","")},function(){t.assertExists("input#query")}),casper.waitForSelector("form input[type=button][value='Get Tweets']",function(){t.assertExists("form input[type=button][value='Get Tweets']");var n=casper.evaluate(function(){return $("#query").val().length});t.assert(n===1e3,"Text input truncated at 1000 characters"),this.click("form input[type=button][value='Get Tweets']")},function(){t.assertExists("form input[type=button][value='Get Tweets']")}),casper.waitForSelector("input[type=button][value='Refresh']",function(){t.assertExists("input[type=button][value='Refresh']"),this.click("input[type=button][value='Refresh']")},function(){this.capture("formRefreshFail.png"),t.assertExists("input[type=button][value='Refresh']")}),casper.run(function(){casper.test.renderResults(!0),casper.test.done()}),casper.test.done()});